import React from 'react';

function App() {
  const name="아기사자";
  const age=23;
  const hobbies=['코딩하기','책읽기','영화보기'];
 const mbti="INFJ";
  const greet =()=> {
    alert(`${name}의 페이지에 오신 것을 환영합니다!`);
  }
  const greeet =()=> {
    alert(`저의 MBTI는 ${mbti} 입니다.`);
  }
  return (
    <div style={{
      padding:'50px',
      fontFamily: 'Pretendard, sans-serif',
      borderRadius: '12px',
      maxWidth:'600px',
      margin: '40px auto',
      boxShadow:'0 8px 16px rgba(0, 0, 0, 0.1)',
      textAlign:'center' 
    }}>
   <h1 style={{ color:'#ee9014', marginBotton:'20px'}}>
    안녕하세요, 저는 {name}입니다!
   </h1>
   <p style={{fontSize:'18px',color:'#555'}}> 
    저는 나이는 {age}살이며, 프론트엔드 개발자를 꿈꾸고 있습니다. 
   </p>
   {age>=20 ?(
    <p style={{color:'#3cb371', fontWeighy: 'bold'}}>
      저는 서울여자대학교 대학생입니다.
    </p>
   ):(
    <p style={{color:'#ff6347', fontWeight:'bold'}}>
      저는 서울여자대학교 대학생을 꿈꾸고 있습니다.
    </p>
   )}
   <h3 style={{color:'#333', marginTop:'30px'}}>🛠️ 제 취미를 소개합니다.</h3>
   <ul style={{listStyle:'none',padding:0}}>
    {hobbies.map((hobby, index)=>(
      <li key={index} style={{
        padding: '8px 16px',
        margin:'8px auto',
        backgroundColor:'#fff',
        borderRadius:'20px',
        boxShadow:'0 2px 6px rgba(0,0,0,0.1)',
        width:'fit-content'
      }}>
        {hobby}
      </li>
    ))}
   </ul>
   <br></br>
   <button onClick={greet} style={{
    display:'block',
    margin:'0 auto',
    marginTop:'30px',
    padding: '12px 24px',
    backgroundColor:'#4a90e2',
    color:'#fff',
    borderRadius:'25px',
    border:'none',
    cursor:'pointer',
    fontSize:'16px',
    boxShadow:'0 4px 8px rgba(74,144,266,0.3'
   }}>
🎉 환영 인사 보기
   </button>
   <button onClick={greeet} style={{
     display:'block',
     margin:'0 auto',
    marginTop:'30px',
    padding: '12px 24px',
    backgroundColor:'pink',
    color:'#fff',
    borderRadius:'25px',
    border:'none',
    cursor:'pointer',
    fontSize:'16px',
    boxShadow:'0 4px 8px rgba(74,144,266,0.3'
   }}>
🧠 mbti 보기
   </button>
   </div>
  );
}

//mbti 변수로
export default App;
