import React from 'react';

function App() {
  const name='아기사자';
  const age=23;
  const major='소프트웨어융합학과';
  const hobbies = ['코딩하기','책읽기','영화보기'];
  const interview={
    q: 'Q. 어떤 개발자가 되고 싶나요?',
    a: 'A. 사용자 경험을 잘 이해하는 따뜻한 개발자가 되고 싶어요.',};

    return (
      <div style={styles.container}>

      <div style={styles.card}>
        <h2>프로필</h2>
        <p>이름: {name}</p>
        <p>나이: {age}살</p>
        <p>목표: 프론트엔드 개발자</p>
      </div>

      <div style={styles.card}>
        <h2>학교</h2>
        <p>학교: 서울여자대학교</p>
        <p>전공: {major}</p>
        {age>=20?<p>저는 서울여자대학교 대학생입니다.</p>:<p>저는 서울여자대학교 대학생을 꿈꾸고 있습니다.</p>}
      </div>

      <div style={styles.card}>
        <h2>취미</h2>
        <ul style={{listStyle:'none',padding: 0}}>
          {hobbies.map((hobby, index)=>(
            <li key={index} style={styles.hobbyItem}>
              {hobby}
            </li>
          ))}
        </ul>
     </div> 

     <div style={styles.card}>
      <h2> 미니 인터뷰 </h2>
      <p>{interview.q}</p>
      <p style={{color:'#4a90e2'}}> {interview.a} </p>
     </div>
      </div>
    );

    const styles = {
      container: {
        maxWidth:'700px',
        margin:'40px auto',
        fontFamily: 'pretendard, sans-serif',
        padding: '20px',
        backgroundColor: '#f9f9fb',
      },
      card: {
        backgroundColor:'#fff',
        padding: '25px',
        borderRadius:'12px',
        boxShadow:'0 4px 10px rgba(0,0,0,0.06)',
        marginBottom:'24px'},

        hobbyItem: {
          backgroundColor:'#f0f4ff',
          padding: '8px 16px',
          margin: '6px auto',
          boderRadius: '16px',
          width: 'fit-content'
        },
    }
};

export default App;
